advance.expression:
expression = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var params = {};
      var markers = [];
      target.getDevicesByCondition(params,function(returnData){
        var loopDevices = function(device){
          return {
            id : device.id,
            icon : {
              normal : {
                url : "../images/ouke/oukeIcon1.svg",
                width : 30,
                height : 30
              },
              highlight : {
                url : "../images/ouke/oukeIcon1.svg",
                width : 50,
                height : 50
              }
            },
            lng : device.values.longitude,
            lat : device.values.latitude,
            infoWindow : {
                title : device.label,
              html : (function(){
                  var rs = "<hr /><table style='width:100%'>";
                  for(var i=0; i<6; i++){
                      rs += "<tr><td style='width:50%'>子系统" + i  + "</td><td style='width:50%'>正常</td></tr>"

                  }
                  rs += "</table>";
                  return rs;
              })()
            },
            rotation : 0,
            minZoom : 0,
            maxZoom : 19,
            on : {
              click : function(){
                target.navigateTo("detail", {
                    tabLabel : device.label,
                    resourceId : device.id
                })
              }
            }
          }
        };
        for(var i in returnData){
          markers.push(loopDevices(returnData[i]));
        };
        console.log(markers);
        target.render({
          center : [116.404, 33.915],
          markers : markers,
          zoom : 6
        });
      })
    },
    highlight : function(event){
      var project = event.params.project;
      event.target.setCenter([project.values.longitude,project.values.latitude]);
      event.target.setZoom(8);
      event.target.setHighlightByMakerId(project.id);
    }
  }
}

{
  on : {
    init : function(event){
      var markers = [];
      var target = event.target;
      event.target.getCurrentProjects(function(projects){
        event.target.queryDomainTree(function(domains){
          var markers = [];
          var loopProject = function(project){
            return {
              id : project.id,
              type : "project",
              data : project,
              icon : {
                normal : {
                  url : "../images/ouke/oukeIcon2.svg",
                  width : 30,
                  height : 30
                },
                highlight : {
                  url : "../images/ouke/oukeIcon2.svg",
                  width : 50,
                  height : 50
                }
              },
              lng : project.values.longitude,
              lat : project.values.latitude,
              infoWindow : {
                html : project.label
              },
              rotation : 0,
              minZoom : 7,
              maxZoom : 19,
              on : {
                click : function(){
                  console.log(project);
                }
              }
            }
          };
          var loopDomain = function(domain){
            return {
              id : domain.id,
              icon : {
                normal : {
                  url : "../images/ouke/oukeIcon1.svg",
                  width : 30,
                  height : 30
                },
                highlight : {
                  url : "../images/ouke/oukeIcon1.svg",
                  width : 50,
                  height : 50
                }
              },
              data : domain,
              lng : domain.values.longitude,
              lat : domain.values.latitude,
              infoWindow : {
                html : domain.label
              },
              rotation : 0,
              minZoom : 0,
              maxZoom : 7,
              on : {
                click : function(){
                  target.setCenter([domain.values.longitude,domain.values.latitude]);
                  target.setZoom(8);
                }
              }
            }
          }
          for(var i in domains[0].domainInfos){
            markers.push(loopDomain(domains[0].domainInfos[i]));
          };
          for(var i in projects){
            markers.push(loopProject(projects[i]));
          };
          event.target.render({
            center : [116.404, 33.915],
            markers : markers,
            zoom : 6
          });
        });
      })
    },
    highlight : function(event){
      var project = event.params.project;
      event.target.setCenter([project.values.longitude,project.values.latitude]);
      event.target.setZoom(8);
      event.target.setHighlightByMakerId(project.id);
    }
  }
}

{
  on : {
    init : function(event){
      var markers = [];
