                width : 50,
                height : 50
              }
            },
            data : device,
            type : "device",
            lng : device.values.longitude,
            lat : device.values.latitude,
            infoWindow : {
              html : device.label
            },
            rotation : 0,
            minZoom : 0,
            maxZoom : 19,
            on : {
              click : function(){
                target.navigateTo("panel", {
                  modelId : device.modelId,
                  resourceId : device.id
