expression = {
    on : {
        init : function(event){
            var target = event.target;
            target.setText("查询API接口函数")
        }
    }
}

expression = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var apiCalObject = {};
      var rs = [];
      for(var i in target.__proto__){
        rs.push({
          id : i,
          label : i
        });
      };
      function setParamValue(obj){
          console.log("setParamValue", obj)
          var regExp = /function ?\((.*)\) ?\{/;
          var funStr = target.__proto__[obj.id].toString();
          var rs = regExp.exec(funStr);
          target.setValue("params", rs[1]);
          target.setValue("funName", obj.label);
          apiCalObject.funName = obj.label;
      };
      target.getEditorStatus("APICALL", function(data){
          apiCalObject = data || {};
          target.setValue("apiCalObject", apiCalObject);
          var find = rs.find(function(elem){
            return elem.id == apiCalObject.funName;
          }) || rs[0];
          var ctrlGroups = [
            [{
              type : "label",
              value : "选择API",
              class : "col-md-2",
              style : {
                  "text-align" : "center"
              }
            },{
              type : "select",
              value : find.id,
              options : rs,
              class : "col-md-10",
              on : {
                change : function(elem){
                  setParamValue(elem.value);
                }
              },
              format : {
                "id" : "id",
                "label" : "label",
                "searchkey" : "label"
              }
            }]
          ];
          target.render(ctrlGroups);
          setTimeout(function(){
            setParamValue(find); 
          });
      });
    }
  }
}

