expression = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var blank = function(num){
        var rs = "";
        for(var i=0; i<num; i++){
          rs += "    "
        }
        return rs;
      }
      target.getEditorStatus("APICALL", function(data){
          if(data){
            apiCalObject = data;
            var funName = apiCalObject.funName;
            var exp = apiCalObject.funs[funName];
            var expression = "function init( event ){\n";
            expression += blank(1) + "var target = event.target;\n";
            expression += blank(1) + "var global = event.global;\n";
            expression += blank(1) + "global.fire(\"renderList\", \"创建代码\");\n";
            expression += "}";
            exp = exp || expression;
            target.render(exp);
          } else {
            target.render("/** 请选择生成表达式 */");
          };
      });
    },
    change : function(event){
      var target = event.target;
      var global = event.global;
      target.setValue("expression", event.value);
    },
    apiChange2 : function(event){
      var target = event.target;
      var global = event.global;
      var pa = event.params;
      var funName = pa.funName;
      var params = pa.params;
      var arr = params ? params.split(", ") : [];
      var kpis = pa.kpis;
      var resources = pa.resources;
      var model = pa.model;
      var blank = function(num){
        var rs = "";
        for(var i=0; i<num; i++){
          rs += "    "
        }
        return rs;
      }
      var expression = blank(0) + "function init( event ){\n";
      expression += blank(1) + "var target = event.target;\n";
      expression += blank(1) + "var global = event.global;\n";
      for(var i in arr){
        if(arr[i] == "modelId"){
          expression += blank(1) + "var modelId = " + model.id + ";\n";
        } else if(arr[i] == "ci"){
          if(resources.length > 0){
            expression += blank(1) + "var ci = [" + resources.map(function(elem){
                return elem.id
              }).toString() + "];\n";
            expression += blank(1) + "var ciNames = [" + resources.map(function(elem){
                return "\"" + elem.label + "\""
              }).toString() + "];\n";
          } else {
            expression += blank(1) + "var ci = [];\n";
          }
        } else if(arr[i] == "kpi"){
          if(kpis.length > 0){
            expression += blank(1) + "var kpi = [" + kpis.map(function(elem){
                return elem.id
              }).toString() + "];\n";
            expression += blank(1) + "var kpiNames = [" + kpis.map(function(elem){
                return "\"" + elem.label + "\""
              }).toString() + "];\n";
          } else {
            expression += blank(1) + "var kpi = [];\n";
          };
        } else if(arr[i] == "extension"){
          expression += blank(1) + "var extension = {};\n";
        } else if(arr[i] == "callback"){

        } else {
          expression += blank(1) + "var " + arr[i] + " = \"\";\n";
        }
      };
      if(arr.indexOf("callback") != -1){
        expression += blank(1) + "target." + funName + "(";
        for(var i in arr){
          if(arr[i] == "callback"){
            expression += "function(returnData){" + "\n";
            expression += blank(2) + "global.fire(\"renderList\", returnData)\n";
            expression += blank(1) + "}";
          } else {
            expression += arr[i]
          }
          if(i < arr.length - 1){
            expression += ",";
          } else {
            expression += ")\n";
          }
        }
      } else {
        expression += blank(1) + "var data = target." + funName + "(";
        if(arr.length > 0){
            for(var i in arr){
              expression += arr[i];
              if(i < arr.length - 1){
                expression += ",";
              } else {
                expression += ");\n";
              }
            };
        } else {
          expression += ");\n";  
        };
      };
      if(arr.indexOf("callback") == -1){
          expression += blank(1) + "global.fire(\"renderList\", data);\n";
      };
      expression += blank(0) + "}";
      target.setText(expression);
      target.setValue("expression", expression);
    }
  }
}

