expression = {
    on : {
        init : function(event){
            var target = event.target;
            target.setText("地图导航");
        }
    }
}

expression = {
  on : {
    init : function(event){
      var markers = [];
      var target = event.target;
      var markers = [];
      var domains = [{
          label : "宝山工业区",
          values : {
              longitude : 121.48,
              latitude : 31.41
          }
      },{
          label : "东山工业区",
          values : {
              longitude : 121.31,
              latitude : 31.56
          }
      },{
          label : "青山工业区",
          values : {
              longitude : 121.34,
              latitude : 32.16
          }
      },{
          label : "梅山工业区",
          values : {
              longitude : 121.41,
              latitude : 31.27
          }
      }]
      var loopDomain = function(domain){
        return {
          id : domain.id,
          icon : {
            normal : {
              url : "../images/ouke/oukeIcon2.svg",
              width : 30,
              height : 30
            },
            highlight : {
              url : "../images/ouke/oukeIcon2.svg",
              width : 50,
              height : 50
            }
          },
          lng : domain.values.longitude,
          lat : domain.values.latitude,
          infoWindow : {
            html : domain.label
          },
          rotation : 0,
          minZoom : 0,
          maxZoom : 19,
          on : {
            click : function(){
              target.navigateTo("navigate", {
                tabLabel : domain.label,
                label : domain.label
              }, "blank");
            }
          }
        }
      }
      for(var i in domains){
        markers.push(loopDomain(domains[i]));
      };
      event.target.render({
        center : [121.48, 31.41],
        markers : markers,
        zoom : 12
      });
    },
    highlight : function(event){
      var project = event.params.project;
      event.target.setCenter([project.values.longitude,project.values.latitude]);
      event.target.setZoom(8);
      event.target.setHighlightByMakerId(project.id);
    }
  }
}

