expression = {
  on : {
    repeat : function(event){
       var colors = [ "#0ca4fe", "#e8b609", "#e74e54" ];
      var target = event.target;
      var global = event.global;
      var ctrlGroups = [];
      var createTr = function(index, element){
        return [{
          type : "jquery",
          render : function(){
              var wrap = $("<div></div>");
              var title = $("<div></div>");
              var bar = $("<div></div>");
              bar.css("width", "10px");
              bar.css("height", "60px");
              bar.css("float");
              bar.css("background-color", colors[index]);
              bar.css("position", "absolute");
              bar.css("right", 0);
              wrap.append(bar);
              title.css("text-align", "right");
              title.css("color", "#a0a0a0");
              title.css("margin-right", "15px");
              title.text(element.name);
              var value = $("<div></div>");
              value.css("color", "#fff");
              value.css("font-size", "30px");
              value.css("text-align", "right");
              value.css("font-weight", "bold");
              value.text(element.value);
              value.css("margin-right", "15px");
              wrap.append(title);
              wrap.append(value);
              return wrap;
          }
        }]
      }
      for(var i = target.$repeatData.data.length - 1; i >= 0; i--){
         ctrlGroups.push(createTr(i, target.$repeatData.data[i]));
      }
      event.target.render(ctrlGroups);
    }
  }
}

