expression = {
  "on" : {
    "init" : function(event){
      var target = event.target;
      target.render();
      target.on("$loadCiKpiComplete", function(cells){
        var angles = cells.filter(function(elem){
          return elem.extend == "angle";
        });
        var switches = cells.filter(function(elem){
          return elem.extend == "value";
        });
        createSimuResKpi = function(inx, element){
          if(!(element.resourceObj && element.kpiObj)){
            element.simulate = true;
            element.value = element.extend == "angle" ? parseInt(Math.random() * 100) : parseInt(Math.round(Math.random()));
          }
          element.resourceObj = element.resourceObj || {
              id : Math.uuid(),
              label : (element.extend == "angle" ? "阀门" : "开关") + inx
            };
          element.nodeId = "number:" + element.resourceObj.id;
          element.kpiObj = element.kpiObj || {
              id : Math.uuid(),
              label : (element.extend == "angle" ? "开度值" : "开关") + inx
            };
          element.kpiId = "number:" + element.kpiObj.id;
          target.triggerSocket({
                nodeId : element.resourceObj.id,
                kpiId : 999999,
                value : 4,
                instance : element.kpiObj.id,
                once : true
            });
        }
        for(var i in cells){
          createSimuResKpi(i, cells[i]);
        }
      });
    },
    click : function(event){
        var target = event.target;
        var tabLabel = target.getParameter("tabLabel")
        target.navigateTo("production", {
            tabLabel :  "厂区1"
        });
    }
  }
}

