expression = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var inx = 0;
      var list = [{
        label : "振动",
        value : parseInt(Math.random() * 10)
      },{
        label : "温度",
        value : parseInt(Math.random() * 10)
      },{
        label : "探伤",
        value : parseInt(Math.random() * 10)
      },{
        label : "油品",
        value : parseInt(Math.random() * 10)
      },{
        label : "磨损",
        value : parseInt(Math.random() * 10)
      }]
      var render = function(){
          var ctrlGroups = [];
          var createTd = function(i, elem){
            return [{
              type : "button",
              value : elem.label,
              icon : "glyphicon glyphicon-chevron-right",
              btnclass : i == inx ? "btn btn-primary" : "btn btn-default",
              class : "col-md-12",
              btnStyle : {
                width : "100%"
              },
              on : {
                click : function(elem){
                    inx = i;
                    render();
                }
              }
            }]
          }
          for(var i in list){
            ctrlGroups.push(createTd(i, list[i]));
          };
          event.target.render(ctrlGroups);
      }
      render();
    }
  }
}

