express = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var dataList = (function(){
        var rs = [];
        var inx = 0;
        for(var i=0; i < 50 * Math.PI; i += (2 * Math.PI /180)){
          rs.push(inx + "");
          inx ++;
        }
        return rs;
      })();
      var valueList = (function(){
        var rs = [];
        for(var i=0; i < 50 * Math.PI; i += (2 * Math.PI /180)){
          var nu = 0;
          for(var j=0; j<8; j++){
            nu += (Math.pow((1 / (j+1)), 3) * Math.sin(i * Math.pow(3 ,j))) * 10 + Math.random() * 1.3;
          };
          rs.push(nu.toFixed(2));
        };
        return rs;
      })();
      var data = [];
      for (var i = 0; i < dataList.length; i++) {
        data.push([dataList[i], valueList[i]])
      }
      var dateList = data.map(function (item) {
        return item[0];
      });
      var valueList = data.map(function (item) {
        return item[1];
      });
      var render = function(init){
        var type = target.getValue("type") || 0;
        var x0 = target.getValue("x0") || 0;
        var x1 = target.getValue("x1") || 180;
        if(type == 0){
            var markLine = undefined;
            var markPoint = undefined;
        } else if(type == 1){
            var markLine = {
              animation: false,
              symbol: 'none',
              silent: false,
              lineStyle: {
                normal: {
                  width : 1,
                  color : "#436fff",
                  type: 'solid'
                }
              },
              label: {
              normal: {
                  show: true,
                  position: 'end',
                  formatter : function(elem){
                    return elem.data.value + "Hz";
                  }
                }
              },
              data : (function(){
                var rs = [];
                for (i = 0; i < 10; i++) {
                  var item = {
                    xAxis: x0 * (i + 2) + ''
                  };
                  rs.push(item)
                }
                /**
                for(var i = 0; i < 10; i++){
                  rs.push({
                    xAxis : i * x0
                  })
                };*/
                return rs;
              })()
            }
            var markPoint = {
                symbol : "circle",
                symbolSize : 8,
              animation: false,
              silent: false,
              label: {
                normal: {
                  show: false
    //                        formatter: '{b}: {d}'
                }
              },
              itemStyle : {
                normal : {
                    color : "#fff",
                    borderColor : "blue"
                }  
              },
              data : (function(){
                var rs = [];
                for (i = 0; i < 10; i++) {
                  var yData = data[x0 / 1 * (i + 2)];
                  if (yData) {
                    var item = {
                      xAxis: x0 * (i + 2) + '',
                      yAxis: yData[1]
                    };
                    rs.push(item)
                  }
                }
                /**
                for(var i = 0; i < 10; i++){
                  rs.push({
                      xAxis : i * x0,
                      yAxis : valueList[i * x0]
                  })
                };*/
                return rs;
              })()
            }
        } else if(type == 2){
            var markLine = {
              animation: false,
              symbol: 'none',
              silent: false,
              lineStyle: {
                normal: {
                    color : "red",
                  type: 'solid'
                }
              },
              label: {
                normal: {
                  show: true,
                  position: 'end',
                  formatter : function(elem){
                    return elem.data.value + "Hz";
                  }
                }
              },
              data : (function(){
                var rs = [];
                for(var i = -5; i < 5; i++){
                  rs.push({
                    xAxis : parseInt(x1) + i * (parseInt(x0) - parseInt(x1))
                  })
                };
                return rs;
              })()
            };
            var markPoint = {
              symbol : "circle",
              symbolSize : 8,
              animation: false,
              silent: false,
              label: {
                normal: {
                  show: false
                }
              },
              itemStyle : {
                normal : {
                    color : "#fff",
                    borderColor : "red"
                }  
              },
              data : (function(){
                var rs = [];
                for(var i = -5; i < 5; i++){
                  rs.push({
                      xAxis : parseInt(x1) + i * (parseInt(x0) - parseInt(x1)),
                      yAxis : valueList[parseInt(x1) + i * (parseInt(x0) - parseInt(x1))]
                  })
                };
                return rs;
              })()
            }
        };
        var option = {
          grid: [{
            "top": "10%",
            "width" : "90%",
            "left" : "5%",
            "height" : "65%"
          }, {
            "top": "10%",
            "width" : "90%",
            "left" : "5%",
            "height" : "65%"
          }],
          tooltip: {
            "alwaysShowContent": true,
            triggerOn: 'none'
          },
          xAxis: [{
            data: dataList,
            axisPointer: {
              label: {
                show: true,
                formatter: function (val) {
                  if (val.seriesData[0]) {
                    target.setValue("x0", val.value);
                    target.setValue("y0", val.seriesData[0].data);
                    target.trigger("handleChange");
                    if(init == false){
                      render();
                    };
                    init = false;
                  }
                  return val.value
                }
              },
              lineStyle: {
                color: "blue",
                width: 2
              },
              handle: {
                show: true,
                size: 30,
                margin: 40,
              },
              "value": x0,
              snap: false,
            }
          }, {
            data: dataList,
            axisPointer: {
              label: {
                formatter: function (val) {
                  if (val.seriesData[0]) {
                    target.setValue("x1", val.value)
                    target.setValue("y1", val.seriesData[0].data);
                    target.trigger("handleChange");
                    init = false;
                  }
                  return val.value
                }
              },
              lineStyle: {
                color: "red",
                width: 2
              },
              handle: {
                show: true,
                size: 30,
                margin: 40,

              },
              "value": x1,
              snap: false,
            },
            gridIndex: 1
          }],
          yAxis: [{
            splitLine: {show: true}

          }, {
            splitLine: {show: false},
            gridIndex: 1
          }],
          dataZoom: [
            {
              show: true,
              realtime: true,
              start: 0,
              end: 10,
              xAxisIndex: [0,1]
            }
          ],
          series: [{
            type: 'line',
            showSymbol: false,
            data:valueList,
            markLine : markLine,
            markPoint : markPoint
          }, {
            type: 'line',
            showSymbol: false,
            lineStyle : {
              normal : {
                opacity : 0
              }
            },
            data:valueList,
            xAxisIndex: 1,
            yAxisIndex: 1
          }]
        };
        option
        target.render(option);
      };
      render();
      target.on("renderChart", function(){
          render();
      })
    }
  }
}

