expression = {
  width : "auto",
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      target.setValue("type", 0);
      target.setValue("x0", undefined);
      target.setValue("x1", undefined);
      var render = function(){
          var x0 = target.getValue("x0") || 0;
          var y0 = target.getValue("y0") || 0;
          var x1 = target.getValue("x1") || 0;
          var y1 = target.getValue("y1") || 0;
          var ctrlGroups = [
           [{
               type : "jquery",
               render : function(){
                   var div = $("<div></div>");
                   div.width(25);
                   div.height(10);
                   div.css("background-color", "blue");
                   return div;
               }
           },{
              type : "label",
              value : "X",
              style : {
                  "width" : "10px",
                  "padding" : "3px"
              }
            },{
              type : "label",
              value : parseFloat(x0).toFixed(4),
              style : {
                  "width" : "50px",
                  "padding" : "3px 10px",
                  "background-color" : "rgba(0,0,0,.1)"
              }
            },{
              type : "label",
              value : "Y",
              style : {
                  "padding" : "3px"
              }
            },{
              type : "label",
              value : parseFloat(y0).toFixed(4),
              style : {
                  "width" : "50px",
                  "padding" : "3px 10px",
                  "background-color" : "rgba(0,0,0,.1)"
              }
            },{
               type : "jquery",
               render : function(){
                   var div = $("<div></div>");
                   div.width(25);
                   div.height(10);
                   div.css("margin-left", "50px");
                   div.css("background-color", "red");
                   return div;
               }
           },{
              type : "label",
              value : "X",
              style : {
                  "padding" : "3px"
              }
            },{
              type : "label",
              value : parseFloat(x1).toFixed(4),
              style : {
                  "width" : "50px",
                  "padding" : "3px 10px",
                  "background-color" : "rgba(0,0,0,.1)"
              }
            },{
              type : "label",
              value : "Y",
              style : {
                  "padding" : "3px"
              }
            },{
              type : "label",
              value : parseFloat(y1).toFixed(4),
              style : {
                  "width" : "50px",
                  "padding" : "3px 10px",
                  "background-color" : "rgba(0,0,0,.1)"
              }
            },{
              type : "label",
              value : "△X",
              style : {
                  "margin-left" : "50px",
                  "padding" : "3px"
              }
            },{
              type : "label",
              value : (x1-x0).toFixed(4),
              style : {
                  "width" : "50px",
                  "padding" : "3px 10px",
                  "background-color" : "rgba(0,0,0,.1)"
              }
            },{
              type : "label",
              value : "△Y",
              style : {
                  "padding" : "3px"
              }
            },{
              type : "label",
              value : (y1-y0).toFixed(4),
              style : {
                  "width" : "50px",
                  "padding" : "3px 10px",
                  "background-color" : "rgba(0,0,0,.1)",
                  "margin-right" : "50px"
              }
            }]
          ];
          target.render(ctrlGroups);
      }
      target.on("handleChange", function(){
          render();
      })
      render();
    }
  }
}

