expression = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var Dic = {
        alertInfo : {
          label : "设备告警信息",
          type : "input"
        },
        abnormal : {
          label : "异常现象",
          type : "select",
          options : [{
            id : 0,
            label : "异常现象"
          }]
        },
        position : {
          label : "检测部位",
          type : "select",
          options : [{
            id : 0,
            label : "轴承座"
          }]
        },
        notice : {
          label : "注意事项",
          type : "select",
          options : [{
            id : 0,
            label : "空载"
          }]
        },
        standard : {
          label : "判断标准",
          type : "select",
          options : [{
            id : 0,
            label : "产品设计图纸"
          }]
        },
        condition : {
          label : "判断依据",
          type : "select",
          options : [{
            id : 0,
            label : "轴承冲击值上升"
          }]
        },
        level : {
          label : "综合状态等级",
          type : "select",
          options : [{
            id : 0,
            label : "轴承冲击值上升"
          }]
        },
        part : {
          label : "异常部位",
          type : "select",
          options : [{
            id : 0,
            label : "轴承冲击值上升"
          }]
        },
        reason : {
          label : "故障产生原因",
          type : "select",
          options : [{
            id : 0,
            label : "轴承冲击值上升"
          }]
        },
        conclusion : {
          label : "综合判断结论",
          type : "select",
          options : [{
            id : 0,
            label : "轴承冲击值上升"
          }]
        },
        suggestion : {
          label : "处理方案建议",
          type : "select",
          options : [{
            id : 0,
            label : "轴承冲击值上升"
          }]
        },
        charger : {
          label : "审核人",
          type : "select",
          options : [{
            id : 0,
            label : "轴承冲击值上升"
          }]
        }
      }
      var ctrlGroups = [];
      var loop = function(item){
        var labelObj = {
          type : "label",
          value : item.label,
          class : "col-md-2"
        }
        var type = item.type;
        var obj = {};
        obj.type = type;
        obj.options = item.options;
        obj.class = "col-md-9";
        obj.colSpan = 2;
        return [labelObj, obj, {
          type : "button",
          value : "",
          icon : "glyphicon glyphicon-plus",
          btnclass : "btn btn-default",
          class : "col-md-1",
          on : {
            click : function(elem){
              target.console(elem);
            }
          }
        }];
      }
      for(var i in Dic){
        ctrlGroups.push(loop(Dic[i]))
      }
      ctrlGroups.push([{
        type : "label",
        value : ""
      },{
        type : "button",
        value : "提交",
        icon : "glyphicon glyphicon-ok",
        btnclass : "btn btn-primary",
        class : "col-md-1",
        on : {
          click : function(elem){
            target.console(elem);
          }
        }
      },{
        type : "button",
        value : "暂存",
        icon : "glyphicon glyphicon-download-alt",
        btnclass : "btn btn-primary",
        class : "col-md-1",
        on : {
          click : function(elem){
            target.console(elem);
          }
        }
      }])
      console.log()
      target.render(ctrlGroups);
    }
  }
}
