expression = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var modelId = 142775081516072;
      var modelName = "螺杆式水冷冷机组 EKSC450F3ST-SZ1624";
      var resourceIds = [215755216796141];
      var resourceNames = ["螺杆水冷冷1号机组"];
      var kpiIds = [144684185476439,144684185476440,144684185476441,144684185476442,144684185476443,144684185476444];
      var kpiNames = ["压缩机1电流1","压缩机1电流2","压缩机1电流3","压缩机2电流2","压缩机2电流3","压缩机1加载脉冲个数"];
      var ctrlGroups = [];
      target.getKpiValueCi(resourceIds, kpiIds, function(valuelist){
        for(var i in kpiIds){
          var find = valuelist.find(function(elem){
            return elem.kpiCode == kpiIds[i];
          });
          ctrlGroups.push([{
              id : kpiIds[i],
            type : "label",
            value : kpiNames[i]
          },{
            type : "label",
            value : find ? find.value : "-"
          }])
        }
        target.render(ctrlGroups);
      });
      target.webSocket(resourceIds, kpiIds, function(data){
          var find = ctrlGroups.find(function(elem){
              return elem[0].id == data.kpiCode
          });
          find.value = data.value;
          target.render(ctrlGroup);
      })
    }
  }
}

