expression = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var groups = target.getRootTarget().groups.filter(function(elem){
          return elem.path != "index";
      })
      var ctrlGroups = [];
      console.log(target.getParameter("path"));
      var path = target.getParameter("path") || groups[0].path;
      var createTr = function(group){
          var str = "default"
          if(group.path == path){
              var str = "primary";
          }
        return [{
          type : "button",
          value : group.label,
          icon : "proudsmart ps-nav_icon_10",
          btnclass : "btn btn-" + str,
          btnStyle : {
              "text-align" : "left",
              width : "100%"
          },
          class : "col-md-12",
          on : {
            click : function(elem){
              target.navigateTo("index", {
                  path : group.path
              }, "self");
            }
          }
        }]
      }
      for(var i in groups){
          ctrlGroups.push(createTr(groups[i]))
      }
      event.target.render(ctrlGroups);
    }
  }
}

