express = {
  on : {
    init : function(event){
      var global = event.global;
      global.fire("changeEchart");
    },
    changeEchart : function(event){
        var target = event.target;
      var global = event.global;
      var p1 = target.getValue("p1") || 0;
      var p2 = target.getValue("p2") || 0;
      var p3 = target.getValue("p3") || 0;
      var p4 = target.getValue("p4") || 0;
      var p5 = target.getValue("p5") || 0;
      console.log(p1,p2,p3,p4,p5);
      var option = {
            title: {
                text: 'XI602'
            },
            tooltip: {},
            legend: {
                data:['频谱']
            },
            xAxis: {
                data: (function(){
                    var rs = [];
                    var inx = 0;
                    for(var i=0; i < 50 * Math.PI; i += (2 * Math.PI /180)){
                        rs.push(inx);
                        inx ++;
                    }
                    return rs;
                })()
            },
            yAxis: {
                max : 100,
                min : -100
            },
            dataZoom: [
                {
                    show: true,
                    realtime: true,
                    start: 0,
                    end: 10,
                    xAxisIndex: [0]
                }
            ],
            series: [{
                name: '波形',
                type: 'line',
                data: (function(){
                    var rs = [];
                    for(var i=0; i < 50 * Math.PI; i += (2 * Math.PI /180)){
                        var nu = 20;
                        for(var j=0; j<4; j++){
                            nu += (Math.pow((1 / (j+1)), 2) * Math.sin(i * Math.pow(4 ,j))) * 10 + Math.random() * 1.3;
                        }
                        rs.push(nu);
                    }
                    return rs;
                })()
            }]
        };
      event.target.render(option);
    }
  }
}

