  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var ctrlGroups = [];
      var list = target.getCtrlGroupDemos();
      var createTd = function(index, element){
        return [{
          type : "button",
          value : element.label,
          icon : "glyphicon glyphicon-asterisk",
          btnclass : "btn btn-default",
          class : "col-md-12",
          btnStyle : {
              "text-align" : "left",
              width : "100%"
          },
          on : {
            click : function(elem){
                target.setValue("config/expression", element.expression);
                global.fire("codeChange");
            }
          }
        }]
      };
      target.setValue("config", {});
      for(var i in list){
        ctrlGroups.push(createTd(i, list[i]))
      };
      event.target.render(ctrlGroups);
    }
  }
}

