expression = {
    on : {
        init : function(event){
            var target = event.target;
            var global = event.global;
            target.render();
        },
        codeChange : function(event){
            var target = event.target;
            var global = event.global;
            var code = target.getValue("config/expression");
            global.fire("pasteBtnChanged");
            target.setText(code);
        },
        change : function(event){
            var target = event.target;
            var global = event.global;
            target.setValue("config/expression", event.value);
            global.fire("pasteBtnChanged");
        }
    }
}

