expression = {
  on : {
    codeRefresh : function(event){
      var target = event.target;
      var global = event.global;
      var code = target.getValue("config/expression");
      //target.setExpression(code);
      var expression =target.runExpression(code);
      var initFn = expression.$attr("on/init");
      if(initFn){
        initFn({
          target : target,
          global : global
        });
      };
    }
  }
}

