series.manual:
(function(data,system){
  var rs = [];
  for(var i in system.mapJson.features){
    rs.push({
      name : system.mapJson.features[i].properties.name,
      value : parseInt(Math.random() * 100)
    })
  }
  return [{
    data : rs
  }];
})

advance.expression:
{
  "on" : {
    "click" : function(event){
      var target = event.global.variable("productlist");
      var value = event.echart.value;
      var number = Math.round(value / 20);
      if(number > 10) {
        number = 10;
      } else if(number < 1) {
        number = 1;
      }
      var result = [];
      for(var j = 0; j < number; j++) {
        var name = ''
        if(j % 4 == 0) {
          name = 'FH (' + event.echart.name + ")";
        } else if(j % 4 == 1) {
          name = 'T系列(' + event.echart.name + ")";
        } else if(j % 4 == 2) {
          name = 'EH系列(' + event.echart.name + ")";
        } else if(j % 4 == 3) {
          name = 'SPZ-FK系列(' + event.echart.name + ")";
        }
        result.push({
          category : {
            label : '产品分类',
            value : name
          },
          product : {
            label : '产品份额',
            value : value + Math.round(Math.random() * 100),
          },
          malfunction : {
            label : '平均故障',
            value : Math.round(Math.random() * 100)
          }
        });
      }
      target.render(result);
    }
  }
}

