expression = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      target.setValue("useDoubleSlash", false)
      var ctrlGroups = [
       [{
          type : "label",
          value : "双斜线",
          class : "col-md-1"
        },{
          type : "toggle",
          class : "col-md-6",
          on : {
            change : function(elem){
                target.setValue("useDoubleSlash", elem.value);
            }
          }
        }]
      ];
      event.target.render(ctrlGroups);
    }
  }
}

expression = {
    on : {
        init : function(event){
            var target = event.target;
            target.render();
        },
        change : function(event){
            var target = event.target;
            var global = event.global;
            var useDoubleSlash = target.getValue("useDoubleSlash");
            console.log(useDoubleSlash);
            if(useDoubleSlash) {
                console.log("a");
                global.fire("setClipBoard", "\"" + event.value.replace(/\\n/g, "\\\\n").replace(/\n/g, "\\n").replace(/\"/g, "\\\"") + "\"");
            } else {
                console.log("b");
                global.fire("setClipBoard", "\"" + event.value.replace(/\n/g, "\\n").replace(/\"/g, "\\\"") + "\"");
            }

        }
    }
}

expression = {
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      global.fire("setClipBoard", "");
    },
    setClipBoard : function(event){
        var target = event.target;
        var params = event.params;
        var ctrlGroups = [
           [{
              type : "clipboardButton",
              value : "转化样式",
              icon : "glyphicon glyphicon-pencil",
              btnclass : "btn btn-primary",
              class : "col-md-12",
              clipboardText : params,
              on : {
                  save : function(){
                      target.growl("已保存到剪切板")
                  }
              }
            }]
          ];
          event.target.render(ctrlGroups);
    }
  }
}
