{
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      event.target.queryDomainTree(function(domaintree){
        event.target.getDomainsByFilter({
          modelIds : [302]
        }, function(projects){
          event.target.setValue("projectName", "");
          event.target.setValue("domainPath", "")
          var ctrlGroups = [
            [{
              type : "label",
              value : "项目名称"
            },{
              type : "autoComplete",
              value : "search",
              options : projects,
              on : {
                change : function(elem){
                  var projectName = elem.value;
                  target.setValue("projectName", projectName);
                }
              },
              format : {
                "id" : "id",
                "label" : "label",
                "searchkey" : "label"
              }
            }],[{
              type : "label",
              value : "管理域"
            },{
              type : "dropdowntree",
              value : "search",
              options : {
                "domainInfos" : domaintree
              },
              on : {
                change : function(elem){
                  var domain = elem.value;
                  target.setValue("domainPath", domain.domainPath);
                }
              },
              format : {
                "children" : "domainInfos"
              }
            }],[{
              type : "label",
              value : ""
            },{
              type : "button",
              value : "搜索",
              icon : "glyphicon glyphicon-search",
              class : "btn-primary",
              on : {
                click : function(elem){
                  global.fire("refresh")
                }
              }
            }]
          ];
          event.target.render(ctrlGroups);
        });
      });
    }
  }
}

{
	"on" : {
    "init" : function(event){
    	event.target.getDomainsByFilter({
        modelIds : [302]
      }, function(projects){
        event.target.render(projects);
      })
    },
    "click" : function(event){
      var project = event.data;
      event.global.fire("highlight", {
        project : project
      })
    },
    "refresh" : function(event){
      var projectName = event.target.getValue("projectName");
      var domainPath = event.target.getValue("domainPath");
      event.target.getDomainsByFilter({
        modelIds : [302]
      }, function(projects){
        var filter = projects.filter(function(elem){
          return (projectName == "" || elem.label.indexOf(projectName) != -1) && (domainPath == "" || elem.domainPath.indexOf(domainPath) != -1);
        })
        event.target.render(filter);
      })
    }
  },
  "format" : [
    {
      "name": "设备名称",
      "value": "label",
      "type": "text",
      "style" : {
        "width" : "100px"
      }
    }
  ]
}

