{
  "event": true,
  "on" : {
    "click" : function(event){
      var id = event.data.id;
      if(id){
        var resource = {
          projectId : id,
          tabLabel : event.data.customerName
        }
        event.ui.navigateTo("","topo", resource, "home");
      } else {
        var values = event.data.value
        event.target.zoomTo(values);
      }
    }
  },
  format : [
    {
      "name": "项目名称",
      "value": "{item:projectName}",
      "type": "text"
    },{
      "name": "项目建立日期",
      "value": "{item:risingTime}",
      "type": "date",
      "style": {
        "color" : "#FFF",
        "background-color" : "#3c8dbc",
        "padding" : "2px 10px",
        "border-radius" : "3px"
      }
    }
  ]
}

{
	"on" : {
    "init" : function(event){
      var param = event.self.getParameter("projectId");
      event.self.getViewByProjectId(param, function(view){
        if(view){
          var json = JSON.parse(view.content);
        	event.self.render(json);
        } else {
          event.self.error("无对应的项目组态视图，请配置后重试.");
        };
      })
    },
    "click" : function(event){
      var modelId = event.ui.modelId;
      var resourceId = event.ui.resourceId;
      var subViewId = event.ui.subViewId;
      console.log(subViewId);
      event.self.getResourceById(resourceId, function(resource){
        if(subViewId && subViewId != 0){
          var param = {
            type : "panel",
            tabLabel : resource.label,
            subViewId : subViewId,
            modelId : modelId,
            resourceId : resourceId
          }
          event.self.navigateTo("subview", param);
        } else {
          var param = {
            tabLabel : resource.label,
            modelId : modelId,
            resourceId : resourceId
          }
          event.self.navigateTo("panel", param);
        }
      });
    }
  }
}

