advance.expression:
{
  on : { 
    click : function(event){
      var excel = event.global.variable('excel');
      var etype = ['EKAA','EKAC','EKAH','EKAS','EKBG','EKCC','EKCE','EKCH','EKCK','EKCW'];
			var rs = [];
      for(var i in etype){
        var rnd = parseInt(Math.random() * 10000);
        rs.push({
          name : etype[i],
          value : rnd
        })
      };
      rs.sort(function(a, b){
        return b.value - a.value;
      });
      var arr = [['设备类型(TOP10)','数量']];
      for(var i in rs){
        var el = [rs[i].name, rs[i].value.toString()];
        arr.push(el);
      }
      excel.render(arr)
    } 
  }
}

