advance.expression:
expression = {
    on : {
        init : function(event){
            var target = event.target;
                 target.setText(target.$repeatData.label);
        }
    }
}

expression = {
  "on" : {
    "repeat" : function(event){
      var temps = [];
      var label = event.target.$repeatData.label;
      var dpe = event.target.$repeatData.data;
      for(var i=0; i<dpe.length; i++){
        temps.push({
          label : dpe[i].name,
          value : dpe[i].num,
          icon : "proudsmart ps-irrigation",
          status : parseInt(Math.random() * 4),
          click : function(){
              event.target.navigateTo('detail', {
                  tabLabel : label
              });
          }
        })
      }
      event.target.render(temps);
    }
  },

  "config" : {
    slidesToShow : 5,
    slidesToScroll : 5
  },
  "render" : function(event){
    var wrap = $("<div></div>")
    var p = $("<p></p>");
    var p2 = $("<p></p>");
    var dom = $("<div></div>");
    var i = $("<i></i>");
    p.css("text-align", "center");
    p2.css("text-align", "center");
    p.text(event.item.value);
    p2.text(event.item.label);
    i.addClass(event.item.icon);
    i.css("font-size", "48px");
    dom.addClass("btn btn-primary");
    var status = event.item.status;
    dom.css("background-color","#3c8dbc");
    dom.css("border", "0");
    dom.css("width", "74px");
    dom.css("height", "67px");
    dom.css("margin", "5px auto");
    dom.css("display", "block");
    dom.append(i);
    wrap.append(p);
    wrap.append(dom);
    wrap.append(p2);
    wrap.on("click",event.item.click);
    return wrap;
  }
}

{
  "on" : {
    "init" : function(event){
      var temps = [];
      for(var i=0; i<10; i++){
        temps.push({
          label : "指标" + i,
          value : parseInt(Math.random() * 100),
          icon : "proudsmart ps-co2",
          status : parseInt(Math.random() * 4)
        })
      }
      event.target.render(temps);
    }
  },
  "config" : {
    slidesToShow : 6,
    slidesToScroll : 6
  },
  "render" : function(event){
    var wrap = $("<div></div>")
    var p = $("<p></p>");
    var p2 = $("<p></p>");
    var dom = $("<div></div>");
    var i = $("<i></i>");
    p.css("text-align", "center");
    p2.css("text-align", "center");
    p.text(event.item.value);
    p2.text(event.item.label);
    i.addClass(event.item.icon);
    i.css("font-size", "48px");
    dom.addClass("btn btn-primary");
    var status = event.item.status;
    dom.css("background-color", event.target.getAlertColor(status));
    dom.css("border", "0");
    dom.css("width", "74px");
    dom.css("height", "67px");
    dom.css("margin", "5px auto");
    dom.css("display", "block");
    dom.append(i);
    wrap.append(p);
    wrap.append(dom);
    wrap.append(p2);
    return wrap;
  }
}

