              position : "absolute",
              top : "-20px"
          },
          render : function(svg){
              var r = 15;
              var g = svg.append("g");
              var circle2 = g.append("circle");
              var circle1 = g.append("circle");
              var repeat;
              var init = function(){
                  circle1.attr("fill", "#098060");
                  circle2.attr("fill", "#0ecf9b");
                  circle1.attr("r", r);
                  circle2.attr("r", r*1.5);
                  g.attr("transform", "translate(50,50)").style("opacity", 1);
              }
              init();
              target.setValue("alarmOn", false);
              target.on("alarm", function(svg){
                  var DURATION = 500
                  if(!target.getValue("alarmOn")){
                      repeat = function(){
                          circle1.transition().duration(DURATION).attr("fill", "#aa0d0d");
                          circle2.transition().duration(DURATION).attr("fill", "#ec550a");
                          g.transition().duration(DURATION)
                          .attr("transform", "translate(50,50),scale(1.5, 1.5)")
                          .style("opacity", .7);
                          setTimeout(function(){
                              circle1.transition().duration(DURATION).attr("fill", "#098060");
                              circle2.transition().duration(DURATION).attr("fill", "#0ecf9b");
                              g.transition().duration(DURATION)
                              .attr("transform", "translate(50,50),scale(1, 1)")
                              .style("opacity", 1);
                              setTimeout(function(){
                                  if(repeat){
                                      repeat();
                                  };
                              }, DURATION)
                          }, DURATION)
                      };
                      repeat();
                  } else {
                      repeat = null;
                  }
                  target.setValue("alarmOn", !target.getValue("alarmOn"))
              });
