expression = {
    on  : {
        repeat : function(event){
            var target = event.target;
            var element = target.$repeatData;
            if(element.simulate){
                var ci = [element.resourceObj.id];
                var kpi = [element.kpiObj.id];
                var extension = {};
                if(!element.value){
                    target.getKpiValueCi(ci,kpi,function(returnData){
                        var val = returnData[0] || {};
                        element.value = val.value;
                        target.render(val.value);
                    },extension);
                } else {
                    target.render(element.value);
                };
            } else {
                target.render(element.value);
            };
        },
        change : function(event){
            var target = event.target;
            var ci = target.$repeatData.resourceObj.id;
            var kpi = target.$repeatData.kpiObj.id;
            target.$repeatData.value = event.value;
            target.triggerSocket({
                nodeId : ci,
                kpiId : kpi,
                value : event.value,
                once : true
            });
        }
    }
}

expression = {
  on : {
    init : function(event){
      console.log("debugger");
      var target = event.target;
      var list = target.getValue("switchData");
      var global = event.global;
      var ctrlGroups = [];
      var createTr = function(inx, element){
        if(element){
          var ci = [element.resourceObj.id];
          var kpi = [element.kpiObj.id];
          var extension = {};
          var pushCtrl = function(){
            var rs = [{
              type : "label",
              value : element.kpiObj.label,
              class : "col-md-6",
              style : {
                "font-weight" : "bold",
                "line-height" : "70px"
              }
            },{
              type : "toggle",
              value : element.value,
              class : "col-md-6",
              style : {
                "text-align" : "right"
              },
              on : {
                change : function(elem){
                    console.log(elem);
                    var ci = element.resourceObj.id;
                    var kpi = element.kpiObj.id;
                    element.value = elem.value;
                    target.triggerSocket({
                        nodeId : ci,
                        kpiId : kpi,
                        value : elem.value ? 1 : 0,
                        once : true
                    })
                }
              }
            }];
            ctrlGroups.push(rs);
            inx++;
            createTr(inx, list[inx]);
          }
          if(!element.value){
              if(!element.simulate){
                  target.getKpiValueCi(ci,kpi,function(returnData){
                  var val = returnData[0] || {};
                  element.value = val.value;
                  pushCtrl();
                },extension);
              } else {
                  pushCtrl();
              }
          } else {
            pushCtrl();
          };
        } else {
          target.render(ctrlGroups);
        }
      };
      var inx = 0;
      createTr(inx, list[inx]);
    }
  }
}

