expression = {
    width : "auto",
  on : {
    init : function(event){
      var target = event.target;
      var global = event.global;
      var pages = target.getPages();
      var ctrlGroups = [];
      var tr = [];
      ctrlGroups.push(tr)
      var createPage = function(i, page){
          return {
              type : "label",
              linkage : true,
              value : page.tabLabel || "标签",
              style : {
                color : "#fff",
                "font-weight" : "bold"
              },
              on : {
                  click : function(event){
                      target.moveTo(page.path);
                  }
              }
          }
      }
      for(var i in pages){
          tr.push(createPage(i, pages[i]));
          if(i < pages.length - 1){
             tr.push({
                type : "label",
                style : {
                    color : "#fff",
                    "font-weight" : "bold"
                  },
                value : " > "
             }); 
          };
      }
      event.target.render(ctrlGroups);
    }
  }
}

