expression = {
  on : {
    init : function(event){
      var target = event.target;
      var viewId = target.getParameter("viewId");
      if(viewId){
        target.getViewById(viewId, function(view){
          var content = view.content;
          var json = JSON.parse(content);
          target.render(json);
        })
      } else {
        target.render();
      }
    },
    click : function(event){
      var target = event.target;
      console.log(event);
      var ui = event.ui;
      var resourceId = ui.resourceId;
      var subViewId = ui.subViewId;
      console.log("subViewId", ui);
      if(subViewId != "0" || subViewId!= undefined){
        target.getViewById(subViewId, function(view){
          target.navigateTo("index", {
            tabLabel : view.viewTitle,
            viewId : subViewId
          });
        })
      }
    }
  }
}
